package com.labs.day1;

public class MultiplicationTableUsingForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		
		int number = 5;
		for(int i =1; i<=10;i++)
		{
			System.out.println(number + "X "+i + "= "+ number *i);
						
		}
		
		//decremental For 
		for (int i=10; i>10; i--)
		{
			System.out.println(i);
		}

		//incremental for
		for (int i =0; i<10; i++) {
			System.out.println(i);
		}
	
	}

}
