package com.labs.day2;

public class WhileDoWhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

      // While loop
      int a=1;
      while( a < 10){
          System.out.println(a);
          a++;

      }

      //// do while loop
      int b=1;
      do{
          System.out.println(b);
          b++;

      }while( b < 10);

	}

}
